definition.script:
pipeline {
    agent any

    stages {
        stage('Test location') {
            steps {
                echo 'location'
                sh 'pwd'
            }
        }

        stage('Téléchargement du repo') {
            steps {
                echo 'Téléchargement du repo'
                sh '''
                    if [ -d "app" ]; then
                        echo "Le dossier app existe déjà, suppression..."
                        rm -rf app
                    fi
                    git clone http://gitlab:8929/root/app
                '''
            }
        }

        stage('Build') {
            steps {
                echo 'Build de l’image Docker de l’app'
                sh 'pwd'
                sh 'docker build -t myapp:latest ./app'
            }
        }

        stage('Test') {
            steps {
                echo 'Tests de l’application'
                sh 'docker run --rm myapp:latest npm test'
            }
        }

        stage('Deploy to Prod') {
            steps {
                echo 'Déploiement de l’image Docker'
                sh '''
                    docker stop myapp || true
                    docker rm myapp || true
                    docker run -d -p 3000:3000 --name myapp myapp:latest
                '''
            }
        }
    }
}
